import {React, useState, useEffect} from 'react'
import FetchApi from './fetchApi/FetchApi'

const Cart = () => {
 const [products, setProducts] = useState(null)
 const [cartProducts, setCartProducts] = useState(null)
  
 useEffect(() => {
   FetchApi('http://localhost:4000/products', 'GET').then(response => setProducts(response))
   FetchApi('http://localhost:4000/cart', 'GET').then(cproducts => setCartProducts(cproducts))
 },[])

 if(!products) return 'Loading'
 if(!cartProducts) return 'Loading'
 
console.log(products)
console.log(cartProducts)

const fCardProductDetails = cartProducts.map(cproduct => {
    let filterproducts = products.filter(product => product.id === cproduct.productId)
    return filterproducts
})

if(!fCardProductDetails) return 'Loading'

console.log(fCardProductDetails)
  return (
    <div style={{minHeight:'90vh'}}>
        
        {fCardProductDetails.map(product => (
             
             <div key={product.id} className="card shadow d-flex flex-column align-items-center" style={{width: '270px', height:"500", background:'linear-gradient(0deg, rgba(34,193,195,1) 0%, rgba(253,187,45,1) 100%)'}} >
             <img className="card-img-top shadow" style={{width: '250px', height:"250px", border:'1px solid red', borderRadius:'5px', margin:'2px' }} src="https://tinyurl.com/59ynrhkt" alt="Card image cap"/>
                <div className='d-flex flex-column ' style={{width:'100%'}}>
                   <div className="card-body align-self-start" >
                    <h5 className="card-title " >{product.productName}</h5>
                    <p > {product.productVersion}</p>
                    </div>
                    <div className='d-flex ' style={{justifyContent:'space-between'}}>
                    <h5 className='text-center'> {`${product.productPrice}/- Rs`}</h5>
                    <i className='fa fa-shopping-bag bg-danger text-white p-2 mx-2 rounded' onClick={() => addItemToCart(product) }/>
                    </div>
                </div>
             </div>

           ))}
      
    </div>
  )
}

export default Cart
