import React from 'react'

import { useSelector, useDispatch } from 'react-redux'
import { removeProductFromCart } from '../features/cartSlice'

const Cart = () => {
  const cart = useSelector((state) => state.cart)
   
   const dispatch = useDispatch()
   
 
   console.log(cart.cartProducts)
  //  if(!cart.cartProducts) return 'Your cart is Empty'
  return (
    <div style={{display:'flex', flexWrap:'wrap', gap:'20px', width:'100%' }}>
    
    {/* {!cart.cartProducts &&  <h1 style={{color:'red'}}> Your Cart is Empty</h1> } */}
 { cart.cartProducts.map(product => (
    <div key={product.id} className="card shadow d-flex flex-column align-items-center" style={{width: '270px', height:"500", background:'#F3F3F3'}} >
    <img className="card-img-top shadow" style={{width: '250px', height:"250px", border:'1px solid red', borderRadius:'5px', margin:'2px' }} src="https://tinyurl.com/59ynrhkt" alt="product"/>
       <div className='d-flex flex-column ' style={{width:'100%'}}>
          {/* <div className="card-body align-self-start" >
           <h5 className="card-title " >{product.productName}</h5>
           <p > {product.productVersion}</p>
           </div> */}
           <div className='d-flex ' style={{justifyContent:'space-between'}}>
           <h5 className='text-center'> {`${product.productPrice}/- Rs`}</h5>
           <i className='text-dark fa fa-shopping-bag' onClick={() => dispatch(removeProductFromCart(product.id))}>Remove Item</i>
           </div>
       </div>
    </div>
  ))} 

</div>
  )
}




export default Cart
