import React from 'react'
import { Link, NavLink } from 'react-router-dom'
import SearchBar from './SearchBar'
import { useSelector } from 'react-redux'
import './header.css'
import Categories from '../categories/Categories'

const Header = () => {
  const products = useSelector((state) => state.cart)
  console.log(products)
  if(!products) return 'Loading...'
  return (
    
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarTogglerDemo02">
    <ul class="navbar-nav mr-auto mt-2 mt-lg-0">
      <li class="nav-item active">
        <a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="#">Link</a>
      </li>
      <li class="nav-item">
        <a class="nav-link disabled" href="#">Disabled</a>
      </li>
    </ul>
    <form class="form-inline my-2 my-lg-0">
      <input class="form-control mr-sm-2" type="search" placeholder="Search"/>
      <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
    </form>
  </div>
</nav>


    // <div className='header-header'>
    //      <div className='header-logo'><Link to='/' className='fw-bold fs-3 navbar-brand'> SHOPPING </Link> </div>
    //      <div className='header-search'>
    //       <SearchBar/>
    //      </div>
    //      <div className='header-menu'>
          
    //       <NavLink to='/cart'> <button className='fa fa-cart-plus btn btn-outline  text-dark p-2 mx-2 rounded border-2 '> <span className='mx-2'> {products.length > 0 ? products.length : null} </span> </button></NavLink>
    //      </div>

    // </div>
    // <Categories/>
    // </>
  )
}

export default Header
