import React from 'react'
import './signup.css'

const Signup = () => {
  return (
    <div>
        <h1>Create a New Account</h1>
        <InputElement type label='Name' placeholder='Enter your name' id='name' />
        <InputElement label='Email' placeholder='Enter your e-mail' id='email' />
    </div>
  )
}

export default Signup

function InputElement ({label, placeholder, id, type,}) {
    return (
      <div className='input-div'>
      <label className='label' htmlFor={id} >{label}</label>
      <input id={id} type={type} className='form-control input-feild' placeholder={placeholder} />
  </div>
    )
}
